INSERT INTO sop_procedures (sop_number, category_id, title, purpose, scope, procedure_steps, status, priority_level, compliance_required, florida_specific, hurricane_related, osha_related, safety_requirements, estimated_duration_minutes, regulatory_compliance, cross_references, legal_citations, verification_sources) VALUES

-- Core Safety Management (1000-1019)
('1001', 2, 'OSHA Compliance Program Management', 'Establish comprehensive OSHA compliance program for construction operations', 'All OSHA-regulated activities and construction safety requirements', '["1. Conduct initial OSHA compliance assessment", "2. Develop written safety program", "3. Establish safety training requirements", "4. Implement hazard identification procedures", "5. Create incident reporting and investigation", "6. Establish safety committee structure", "7. Define disciplinary action procedures", "8. Implement regular safety audits", "9. Monitor OSHA regulatory changes", "10. Update safety program annually"]', 'active', 'critical', true, false, false, true, 'All employees must receive OSHA 10-hour training minimum; supervisors require OSHA 30-hour training', 240, '["OSHA_1926", "OSHA_COMPLIANCE"]', '["1002", "1003", "1004"]', '["CFR_1926", "OSHA_STANDARDS"]', '["OSHA", "National Safety Council", "Associated General Contractors Safety"]'),

('1002', 2, 'Personal Protective Equipment (PPE) Program', 'Ensure proper selection, use, and maintenance of personal protective equipment', 'All employees and subcontractors requiring PPE for roofing operations', '["1. Conduct PPE hazard assessment for all job tasks", "2. Select appropriate PPE based on hazards", "3. Provide PPE training to all employees", "4. Establish PPE inspection procedures", "5. Implement PPE maintenance and replacement", "6. Create PPE compliance monitoring", "7. Document PPE training and compliance", "8. Establish disciplinary procedures for non-compliance", "9. Review PPE effectiveness regularly", "10. Update PPE program based on new hazards"]', 'active', 'critical', true, false, false, true, 'Hard hats, safety glasses, fall protection harnesses, non-slip footwear required for all roof work; additional PPE based on specific hazards', 120, '["OSHA_1926_95", "PPE_STANDARDS"]', '["1001", "1003", "1010"]', '["CFR_1926_95", "ANSI_Z89_1"]', '["OSHA", "American National Standards Institute", "International Safety Equipment Association"]'),

('1003', 2, 'Fall Protection Program', 'Prevent falls from roofs and elevated work surfaces', 'All work performed at heights of 6 feet or greater', '["1. Identify all fall hazards on job sites", "2. Develop fall protection plan for each project", "3. Select appropriate fall protection systems", "4. Train employees on fall protection equipment", "5. Inspect fall protection equipment daily", "6. Establish rescue and emergency procedures", "7. Document fall protection training and inspections", "8. Monitor fall protection compliance", "9. Investigate any fall incidents", "10. Update fall protection program regularly"]', 'active', 'critical', true, false, false, true, 'Full body harnesses, lanyards, and anchor points required for all roof work; 100% tie-off required', 180, '["OSHA_1926_501", "FALL_PROTECTION"]', '["1001", "1002", "1004"]', '["CFR_1926_501", "ANSI_Z359"]', '["OSHA", "Fall Protection Industry Association", "International Association of Safety Professionals"]'),

('1004', 2, 'Ladder Safety Program', 'Ensure safe use and maintenance of ladders', 'All ladder use in construction and roofing operations', '["1. Inspect ladders before each use", "2. Train employees on proper ladder setup", "3. Establish ladder placement and angle requirements", "4. Define load capacity and usage limits", "5. Implement ladder maintenance procedures", "6. Create ladder storage and transport protocols", "7. Establish three-point contact rule", "8. Define weather and environmental restrictions", "9. Document ladder incidents and near-misses", "10. Replace damaged ladders immediately"]', 'active', 'critical', true, false, false, true, 'Ladder inspections required before each use; 4:1 setup ratio; three-point contact maintained at all times', 90, '["OSHA_1926_1053", "LADDER_SAFETY"]', '["1002", "1003", "1005"]', '["CFR_1926_1053", "ANSI_A14"]', '["OSHA", "American Ladder Institute", "National Safety Council"]'),

('1005', 2, 'Roof Safety and Access Procedures', 'Establish safe access and work procedures for roof operations', 'All roofing work and roof access activities', '["1. Assess roof conditions before access", "2. Establish safe access and egress routes", "3. Identify and mark roof hazards", "4. Implement weather monitoring procedures", "5. Define roof work area controls", "6. Establish emergency evacuation procedures", "7. Create roof material storage protocols", "8. Implement hot work permit procedures", "9. Monitor roof structural integrity", "10. Document daily roof safety inspections"]', 'active', 'critical', true, true, false, true, 'Fall protection required for all roof work; weather monitoring essential; structural assessment required before material loading', 150, '["OSHA_1926_501", "ROOF_SAFETY", "FL_BUILDING_CODE"]', '["1003", "1006", "1007"]', '["CFR_1926_501", "FBC_ROOFING"]', '["OSHA", "National Roofing Contractors Association", "Roof Safety Institute"]'),

('1006', 2, 'Tool and Equipment Safety', 'Ensure safe operation and maintenance of tools and equipment', 'All power tools, hand tools, and equipment used in roofing operations', '["1. Inspect tools and equipment before each use", "2. Train employees on proper tool operation", "3. Establish tool maintenance schedules", "4. Implement lockout/tagout procedures", "5. Define tool storage and security protocols", "6. Create equipment operator certification", "7. Establish tool defect reporting procedures", "8. Implement tool safety training programs", "9. Monitor tool-related incidents", "10. Update tool safety procedures regularly"]', 'active', 'high', true, false, false, true, 'Daily tool inspections required; proper PPE for tool operation; lockout/tagout for electrical equipment', 120, '["OSHA_1926", "TOOL_SAFETY"]', '["1002", "1007", "1008"]', '["CFR_1926_300", "ANSI_TOOL_STANDARDS"]', '["OSHA", "Power Tool Institute", "Hand Tools Institute"]'),

('1007', 2, 'Hot Work Safety Program', 'Control fire hazards during welding, cutting, and hot work operations', 'All welding, cutting, torch work, and other hot work activities', '["1. Obtain hot work permits before beginning work", "2. Conduct pre-work fire hazard assessment", "3. Establish fire watch procedures", "4. Provide appropriate fire extinguishers", "5. Clear combustible materials from work area", "6. Establish hot work isolation procedures", "7. Train employees on hot work safety", "8. Monitor work area during and after operations", "9. Document hot work permits and inspections", "10. Investigate any fire incidents"]', 'active', 'critical', true, false, false, true, 'Hot work permits required; fire watch mandatory; appropriate fire extinguishers within 25 feet', 90, '["OSHA_1926", "FIRE_SAFETY", "NFPA_51B"]', '["1005", "1006", "1008"]', '["CFR_1926_352", "NFPA_51B"]', '["OSHA", "National Fire Protection Association", "American Welding Society"]'),

('1008', 2, 'Electrical Safety Program', 'Prevent electrical hazards and ensure safe electrical work practices', 'All electrical work and operations around electrical hazards', '["1. Identify electrical hazards on job sites", "2. Implement lockout/tagout procedures", "3. Provide electrical safety training", "4. Establish qualified person requirements", "5. Create electrical work permit procedures", "6. Implement ground fault circuit interrupter (GFCI) protection", "7. Define overhead power line clearances", "8. Establish electrical equipment inspection", "9. Create electrical emergency procedures", "10. Document electrical safety training"]', 'active', 'critical', true, false, false, true, 'GFCI protection required for all temporary electrical; qualified electricians for electrical work; minimum clearances from power lines', 150, '["OSHA_1926_400", "ELECTRICAL_SAFETY", "NFPA_70E"]', '["1006", "1007", "1009"]', '["CFR_1926_400", "NFPA_70E"]', '["OSHA", "National Fire Protection Association", "Electrical Safety Foundation International"]'),

('1009', 2, 'Material Handling and Storage Safety', 'Ensure safe handling, lifting, and storage of roofing materials', 'All material handling, storage, and transportation activities', '["1. Assess material weights and handling requirements", "2. Train employees on proper lifting techniques", "3. Implement mechanical lifting equipment procedures", "4. Establish material storage area requirements", "5. Create material delivery and unloading protocols", "6. Define load capacity and weight limits", "7. Implement material securing and tie-down procedures", "8. Establish housekeeping and storage standards", "9. Monitor material handling incidents", "10. Update procedures based on new materials"]', 'active', 'high', true, true, false, true, 'Proper lifting techniques; mechanical aids for heavy materials; material storage must not exceed roof load limits', 120, '["OSHA_1926", "MATERIAL_HANDLING", "FL_BUILDING_CODE"]', '["1005", "1010", "1011"]', '["CFR_1926_250", "FBC_STRUCTURAL"]', '["OSHA", "Material Handling Industry Association", "National Safety Council"]'),

('1010', 2, 'Vehicle and Mobile Equipment Safety', 'Ensure safe operation of vehicles and mobile equipment', 'All company vehicles, equipment transport, and mobile equipment operations', '["1. Establish driver qualification requirements", "2. Implement vehicle inspection procedures", "3. Define safe driving policies and procedures", "4. Create mobile equipment operation protocols", "5. Establish vehicle maintenance schedules", "6. Implement accident reporting procedures", "7. Provide defensive driving training", "8. Define load securing and transport procedures", "9. Monitor driving records and violations", "10. Update vehicle safety program regularly"]', 'active', 'high', true, true, false, false, 'Valid commercial driver license for applicable vehicles; daily vehicle inspections; defensive driving training annually', 90, '["DOT_REGULATIONS", "VEHICLE_SAFETY", "FL_TRANSPORTATION"]', '["1009", "1011", "1012"]', '["CFR_PART_395", "FL_MOTOR_VEHICLE_CODE"]', '["Department of Transportation", "Federal Motor Carrier Safety Administration", "Florida Department of Transportation"]'),

('1011', 2, 'Hazard Communication Program', 'Communicate chemical hazards and ensure safe handling of hazardous materials', 'All chemicals, solvents, adhesives, and hazardous materials used in operations', '["1. Maintain inventory of hazardous chemicals", "2. Obtain and maintain Safety Data Sheets (SDS)", "3. Implement chemical labeling requirements", "4. Provide hazard communication training", "5. Establish chemical storage procedures", "6. Create chemical emergency procedures", "7. Implement personal protective equipment for chemicals", "8. Define chemical waste disposal procedures", "9. Monitor employee chemical exposure", "10. Update chemical inventory regularly"]', 'active', 'critical', true, false, false, true, 'SDS sheets accessible to all employees; chemical-specific PPE required; proper ventilation for chemical use', 120, '["OSHA_1926_59", "HAZCOM", "EPA_REGULATIONS"]', '["1002", "1012", "1013"]', '["CFR_1926_59", "EPA_RCRA"]', '["OSHA", "Environmental Protection Agency", "Chemical Safety Board"]'),

('1012', 2, 'Emergency Response and First Aid', 'Respond to workplace emergencies and provide immediate medical care', 'All workplace emergencies, injuries, and medical situations', '["1. Establish emergency response team structure", "2. Provide first aid and CPR training", "3. Create emergency contact procedures", "4. Establish medical emergency response protocols", "5. Maintain first aid supplies and equipment", "6. Define evacuation procedures", "7. Create communication systems for emergencies", "8. Establish injury reporting and documentation", "9. Conduct emergency response drills", "10. Review and update emergency procedures"]', 'active', 'critical', true, true, true, true, 'First aid certified personnel on all job sites; emergency communication systems; weather emergency procedures for hurricanes', 180, '["OSHA_1926", "FIRST_AID", "EMERGENCY_RESPONSE"]', '["1001", "1013", "1100"]', '["CFR_1926_50", "OSHA_FIRST_AID"]', '["OSHA", "American Red Cross", "National Safety Council"]'),

('1013', 2, 'Incident Investigation and Reporting', 'Investigate workplace incidents and implement corrective actions', 'All workplace incidents, near-misses, and safety-related events', '["1. Establish incident reporting procedures", "2. Define investigation team responsibilities", "3. Create incident investigation methodology", "4. Implement evidence collection procedures", "5. Establish root cause analysis techniques", "6. Define corrective action development", "7. Create incident documentation requirements", "8. Establish follow-up and verification procedures", "9. Implement lessons learned communication", "10. Monitor incident trends and patterns"]', 'active', 'critical', true, false, false, true, 'Immediate incident reporting required; preserve accident scene; witness interviews; root cause analysis mandatory', 150, '["OSHA_1926", "INCIDENT_INVESTIGATION"]', '["1001", "1012", "1014"]', '["CFR_1926_95", "OSHA_REPORTING"]', '["OSHA", "National Safety Council", "System Safety Society"]'),

('1014', 2, 'Safety Training and Education Program', 'Provide comprehensive safety training to all employees', 'All employees, subcontractors, and safety training requirements', '["1. Assess training needs for all positions", "2. Develop position-specific training curricula", "3. Establish new employee orientation programs", "4. Create ongoing safety training schedules", "5. Implement competency assessment procedures", "6. Establish trainer qualification requirements", "7. Define training documentation and records", "8. Create refresher training programs", "9. Monitor training effectiveness", "10. Update training based on incidents and changes"]', 'active', 'critical', true, false, false, true, 'OSHA 10-hour minimum for all employees; OSHA 30-hour for supervisors; task-specific training required', 240, '["OSHA_1926", "SAFETY_TRAINING"]', '["1001", "1015", "6001"]', '["CFR_1926_95", "OSHA_TRAINING_REQUIREMENTS"]', '["OSHA", "National Safety Council", "Board of Certified Safety Professionals"]'),

('1015', 2, 'Respiratory Protection Program', 'Protect employees from airborne hazards through respiratory protection', 'All operations with potential airborne hazards including dust, fumes, and vapors', '["1. Conduct workplace assessment for respiratory hazards", "2. Select appropriate respiratory protection equipment", "3. Provide medical evaluations for respirator users", "4. Conduct respirator fit testing", "5. Establish respirator maintenance and storage", "6. Provide respiratory protection training", "7. Create emergency and rescue procedures", "8. Implement program evaluation procedures", "9. Maintain respiratory protection records", "10. Update program based on hazard changes"]', 'active', 'high', true, false, false, true, 'Medical clearance required for respirator use; annual fit testing; proper respirator selection based on hazards', 180, '["OSHA_1926_103", "RESPIRATORY_PROTECTION"]', '["1002", "1011", "1016"]', '["CFR_1926_103", "ANSI_Z88_2"]', '["OSHA", "International Safety Equipment Association", "American Industrial Hygiene Association"]'),

('1016', 2, 'Hearing Conservation Program', 'Protect employees from noise-induced hearing loss', 'All operations with noise exposure at or above 85 decibels', '["1. Conduct noise exposure assessments", "2. Implement engineering and administrative controls", "3. Provide hearing protection equipment", "4. Establish audiometric testing programs", "5. Provide hearing conservation training", "6. Maintain hearing protection equipment", "7. Document noise exposure and training", "8. Monitor program effectiveness", "9. Investigate hearing loss cases", "10. Update program based on exposure changes"]', 'active', 'high', true, false, false, true, 'Noise exposure monitoring required; hearing protection in high-noise areas; annual audiometric testing', 120, '["OSHA_1926_52", "HEARING_CONSERVATION"]', '["1002", "1015", "1017"]', '["CFR_1926_52", "ANSI_S3_34"]', '["OSHA", "National Hearing Conservation Association", "American Speech-Language-Hearing Association"]'),

('1017', 2, 'Heat Illness Prevention Program', 'Prevent heat-related illnesses in outdoor roofing operations', 'All outdoor work operations, particularly during hot weather conditions', '["1. Monitor weather conditions and heat index", "2. Implement work/rest schedules for hot weather", "3. Provide adequate water and shade", "4. Train employees on heat illness recognition", "5. Establish acclimatization procedures for new workers", "6. Create emergency response for heat illness", "7. Implement cooling measures and equipment", "8. Monitor employees for heat illness symptoms", "9. Establish high-heat work procedures", "10. Update procedures based on weather patterns"]', 'active', 'critical', true, true, false, true, 'Mandatory water breaks every 15-30 minutes in high heat; shade structures required; heat illness training for supervisors', 90, '["OSHA_HEAT_SAFETY", "HEAT_ILLNESS_PREVENTION", "FL_HEAT_SAFETY"]', '["1002", "1012", "1018"]', '["OSHA_HEAT_GUIDANCE", "NIOSH_HEAT_CRITERIA"]', '["OSHA", "National Institute for Occupational Safety and Health", "Florida Department of Health"]'),

('1018', 2, 'Chemical Safety and Storage', 'Safely handle, store, and use chemicals and hazardous materials', 'All chemicals, solvents, adhesives, and hazardous materials', '["1. Classify chemicals by hazard categories", "2. Establish chemical storage compatibility", "3. Implement chemical handling procedures", "4. Create spill response and cleanup procedures", "5. Establish chemical disposal procedures", "6. Provide chemical-specific PPE", "7. Implement ventilation requirements", "8. Create chemical emergency procedures", "9. Monitor chemical exposure levels", "10. Update procedures for new chemicals"]', 'active', 'critical', true, false, false, true, 'Chemical-specific PPE required; proper storage and segregation; spill kits available; ventilation systems operational', 150, '["OSHA_1926_59", "EPA_RCRA", "CHEMICAL_SAFETY"]', '["1011", "1019", "1050"]', '["CFR_1926_59", "EPA_RCRA", "DOT_HAZMAT"]', '["OSHA", "Environmental Protection Agency", "Chemical Safety Board"]'),

('1019', 2, 'Confined Space Safety Program', 'Ensure safe entry and work in confined spaces', 'All permit-required confined spaces and confined space operations', '["1. Identify and classify confined spaces", "2. Develop permit-required confined space procedures", "3. Establish atmospheric testing requirements", "4. Implement ventilation and isolation procedures", "5. Define entry permit and authorization process", "6. Establish attendant and entry supervisor roles", "7. Provide confined space training", "8. Create emergency and rescue procedures", "9. Maintain confined space equipment", "10. Review and update confined space program"]', 'active', 'critical', true, false, false, true, 'Atmospheric testing before entry; continuous monitoring; rescue procedures established; permit system required', 180, '["OSHA_1926_1200", "CONFINED_SPACE"]', '["1008", "1015", "1020"]', '["CFR_1926_1200", "ANSI_Z117_1"]', '["OSHA", "International Association of Fire Chiefs", "Confined Space Rescue Association"]'),

('1020', 2, 'Crane and Rigging Safety', 'Ensure safe operation of cranes and rigging equipment', 'All crane operations and rigging activities for material handling', '["1. Conduct crane inspections before use", "2. Verify crane operator certifications", "3. Establish load capacity and rigging procedures", "4. Implement signaling and communication protocols", "5. Define exclusion zones and safety barriers", "6. Establish rigging inspection procedures", "7. Create lift planning and review processes", "8. Implement weather restrictions for crane operations", "9. Provide crane and rigging training", "10. Document crane operations and maintenance"]', 'active', 'critical', true, false, false, true, 'Certified crane operators required; daily crane inspections; load calculations verified; exclusion zones established', 240, '["OSHA_1926_1400", "CRANE_SAFETY"]', '["1006", "1009", "1021"]', '["CFR_1926_1400", "ASME_B30"]', '["OSHA", "Crane Institute Certification", "Specialized Carriers & Rigging Association"]'),

-- Weather and Environmental Safety (1050-1099)
('1050', 2, 'Environmental Compliance Program', 'Ensure compliance with environmental regulations and protect natural resources', 'All environmental aspects of construction and roofing operations', '["1. Identify environmental regulatory requirements", "2. Conduct environmental impact assessments", "3. Implement pollution prevention measures", "4. Establish waste management procedures", "5. Create spill prevention and response plans", "6. Implement stormwater management controls", "7. Monitor environmental compliance", "8. Provide environmental training", "9. Document environmental activities", "10. Update environmental program regularly"]', 'active', 'high', true, true, false, false, 'Stormwater pollution prevention; waste segregation and disposal; spill prevention and response', 120, '["EPA_REGULATIONS", "FDEP_REQUIREMENTS", "CLEAN_WATER_ACT"]', '["1018", "1051", "1052"]', '["CLEAN_WATER_ACT", "RCRA", "FL_ENVIRONMENTAL_CODE"]', '["Environmental Protection Agency", "Florida Department of Environmental Protection"]'),

('1051', 2, 'Waste Management and Disposal', 'Properly manage and dispose of construction waste and hazardous materials', 'All waste generated from roofing and construction operations', '["1. Classify waste by type and hazard level", "2. Establish waste segregation procedures", "3. Implement waste reduction strategies", "4. Create waste storage and containment procedures", "5. Establish waste disposal and recycling programs", "6. Provide waste handling training", "7. Maintain waste manifests and documentation", "8. Monitor waste disposal compliance", "9. Implement hazardous waste procedures", "10. Update waste management procedures"]', 'active', 'high', true, true, false, false, 'Proper waste segregation; hazardous waste manifests; approved disposal facilities only', 90, '["EPA_RCRA", "FDEP_WASTE", "HAZARDOUS_WASTE"]', '["1050", "1052", "1018"]', '["RCRA", "FL_HAZARDOUS_WASTE"]', '["Environmental Protection Agency", "Florida Department of Environmental Protection"]'),

('1052', 2, 'Stormwater Management and Erosion Control', 'Prevent stormwater pollution and control erosion at construction sites', 'All construction sites with potential stormwater impact', '["1. Develop site-specific stormwater pollution prevention plan", "2. Install erosion and sediment control measures", "3. Implement stormwater best management practices", "4. Conduct regular inspections of control measures", "5. Maintain stormwater control systems", "6. Monitor and document stormwater compliance", "7. Provide stormwater training to employees", "8. Respond to stormwater violations", "9. Update stormwater plans as needed", "10. Coordinate with local stormwater authorities"]', 'active', 'high', true, true, false, false, 'Stormwater permit compliance; erosion control installation and maintenance', 120, '["CLEAN_WATER_ACT", "NPDES", "FL_STORMWATER"]', '["1050", "1051", "1100"]', '["CLEAN_WATER_ACT", "NPDES_PERMIT"]', '["Environmental Protection Agency", "Florida Department of Environmental Protection"]'),

-- Hurricane and Weather Safety (1100-1119)
('1100', 2, 'Hurricane Preparedness and Response Plan', 'Prepare for and respond to hurricane threats and impacts', 'All operations during hurricane season and severe weather events', '["1. Monitor National Hurricane Center forecasts", "2. Implement hurricane watch and warning procedures", "3. Secure job sites and equipment", "4. Establish employee communication and accountability", "5. Create evacuation and shelter procedures", "6. Implement post-storm assessment and recovery", "7. Coordinate with emergency management authorities", "8. Establish business continuity procedures", "9. Document hurricane response activities", "10. Update hurricane plan based on lessons learned"]', 'active', 'critical', true, true, true, true, 'Hurricane tracking and communication systems; job site securing procedures; employee evacuation protocols', 180, '["HURRICANE_PREPAREDNESS", "EMERGENCY_MANAGEMENT", "FL_EMERGENCY_MGMT"]', '["1101", "1102", "0032"]', '["FEMA_GUIDELINES", "FL_EMERGENCY_MGMT", "NHC_PROCEDURES"]', '["National Hurricane Center", "Florida Division of Emergency Management", "Federal Emergency Management Agency"]'),

