-- Update second section completion
UPDATE sop_procedures SET
    created_at = CURRENT_TIMESTAMP,
    updated_at = CURRENT_TIMESTAMP,
    effective_date = DATE('now'),
    next_review_date = DATE('now', '+12 months'),
    version = '1.0',
    created_by = 'System Administrator',
    content_file_path = 'sop-content/sop-' || sop_number || '.md'
WHERE category_id = 2 AND sop_number LIKE '10%';

-- =====================================================
-- ANALYSIS AND VERIFICATION QUERIES
-- =====================================================

-- Verify SOP counts by category
SELECT
    sc.category_name,
    COUNT(sp.id) as sop_count,
    COUNT(CASE WHEN sp.florida_specific = true THEN 1 END) as florida_specific_count,
    COUNT(CASE WHEN sp.hurricane_related = true THEN 1 END) as hurricane_related_count,
    COUNT(CASE WHEN sp.osha_related = true THEN 1 END) as osha_related_count,
    COUNT(CASE WHEN sp.compliance_required = true THEN 1 END) as compliance_required_count
FROM sop_categories sc
LEFT JOIN sop_procedures sp ON sc.id = sp.category_id
GROUP BY sc.id, sc.category_name
ORDER BY sc.sort_order;

-- Verify total SOP count
SELECT
    'Total SOPs Created' as status,
    COUNT(*) as count
FROM sop_procedures;

-- Verify compliance statistics
SELECT
    'Florida Specific SOPs' as status,
    COUNT(*) as count
FROM sop_procedures
WHERE florida_specific = true
UNION ALL
SELECT
    'Hurricane Related SOPs' as status,
    COUNT(*) as count
FROM sop_procedures
WHERE hurricane_related = true
UNION ALL
SELECT
    'OSHA Required SOPs' as status,
    COUNT(*) as count
FROM sop_procedures
WHERE osha_related = true
UNION ALL
SELECT
    'Critical Priority SOPs' as status,
    COUNT(*) as count
FROM sop_procedures
WHERE priority_level = 'critical';

-- Performance optimization
ANALYZE sop_procedures;
ANALYZE sop_categories;

PRAGMA optimize;

-- =====================================================
-- COMPLETION STATUS
-- =====================================================

SELECT
    'Foundation and Governance SOPs' as section,
    COUNT(*) as count
FROM sop_procedures
WHERE category_id = 1
UNION ALL
SELECT
    'Safety and Compliance SOPs (Partial)' as section,
    COUNT(*) as count
FROM sop_procedures
WHERE category_id = 2;

